import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps,
  Theme
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

interface State {
  rating: number;
  isFocused: boolean;
}

class StarRatings extends StreamlitComponentBase<State> {
  public state: State = { rating: 0, isFocused: false };

  public render = (): ReactNode => {
    const { rating, isFocused } = this.state;
    const { args, disabled, theme }: ComponentProps & { theme?: Theme } = this.props;

    const numStars: number = args["numStars"] || 5; // Number of stars passed from Python

    // Use optional chaining to safely access properties of the theme object
    const primaryColor = theme?.primaryColor || "gray";

    const style: React.CSSProperties = {
      border: `1px solid ${isFocused ? primaryColor : "gray"}`,
      outline: `1px solid ${isFocused ? primaryColor : "gray"}`,
    };

    return (
      <div>
        <div>
          {[...Array(numStars)].map((_, index) => (
            <span
              key={index}
              onClick={() => this.handleRatingChange(index + 1)}
              onMouseMove={(event) => this.handleMouseMove(event, index + 1)}
              onMouseLeave={() => this.handleMouseLeave()}
              style={{ cursor: "pointer", color: index < rating ? "gold" : "gray" }}
              onFocus={this._onFocus}
              onBlur={this._onBlur}
            >
              {this.getStarSymbol(index + 1)}
            </span>
          ))}
        </div>
      </div>
    );
  };

  private handleRatingChange = (rating: number): void => {
    this.setState({ rating }, () => Streamlit.setComponentValue(rating));
  };

  private handleMouseMove = (event: React.MouseEvent, index: number): void => {
    const rect = event.currentTarget.getBoundingClientRect();
    const offsetX = event.clientX - rect.left;
    const percentage = offsetX / rect.width;
    let newRating = index + percentage;

    // Round to the nearest half-star
    newRating = Math.round(newRating * 2) / 2;

    this.setState({ rating: newRating });
  };

  private handleMouseLeave = (): void => {
    // Reset rating to the whole number
    this.setState((prevState) => ({ rating: Math.round(prevState.rating) }));
  };

  private getStarSymbol = (index: number): string => {
    const roundedRating = Math.round(this.state.rating);
    if (this.state.rating - index === 0.5) {
      return "☆"; // Half-star symbol
    } else if (index <= roundedRating) {
      return "★"; // Full-star symbol
    } else {
      return "☆"; // Empty-star symbol
    }
  };

  private _onFocus = (): void => {
    this.setState({ isFocused: true })
  }

  private _onBlur = (): void => {
    this.setState({ isFocused: false })
  }
}

export default withStreamlitConnection(StarRatings)





