import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps, Theme
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

interface State {
  rating: number;
  hoverRating: number;
  isFocused: boolean;
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class StarRatings extends StreamlitComponentBase<State> {
  public state: State = { rating: 0, hoverRating: 0, isFocused: false };

  public render = (): ReactNode => {
    // Arguments that are passed to the plugin in Python are accessible
    // via `this.props.args`. Here, we access the "name" arg.
    const { rating, isFocused } = this.state;
    const { args, disabled, theme }: ComponentProps & { theme?: Theme } = this.props;

    const numStars: number = args["numStars"] || 5;
    const name: string = args["name"] || "";

    // Streamlit sends us a theme object via props that we can use to ensure
    // that our component has visuals that match the active theme in a
    // streamlit app.
    // const { theme } = this.props
    const primaryColor = theme?.primaryColor || "gray";
    const style: React.CSSProperties = {
      border: `1px solid ${isFocused ? primaryColor : "gray"}`,
      outline: `1px solid ${isFocused ? primaryColor : "gray"}`,
    };

    // Maintain compatibility with older versions of Streamlit that don't send
    // a theme object.
    if (theme) {
      // Use the theme object to style our button border. Alternatively, the
      // theme style is defined in CSS vars.
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : "gray"
      }`
      style.border = borderStyling
      style.outline = borderStyling
    }

    // Show a button and some text.
    // When the button is clicked, we'll increment our "numClicks" state
    // variable, and send its new value back to Streamlit, where it'll
    // be available to the Python program.
    return (
      <div>
        <span>Hello {name},</span>
        <div>
          {[...Array(numStars)].map((_, index) => (
            <span
              key={index}
              onMouseEnter={() => this.handleMouseEnter(index + 1)}
              onMouseLeave={this.handleMouseLeave}
              onClick={() => this.handleRatingChange(index + 1)}
              style={{
                cursor: "pointer",
                color: index < (hoverRating || rating) ? "gold" : "grey"
              }}
            >
              {this.renderStar(index + 1)}
            </span>
          ))}
        </div>
      </div>
    );
  };
  /** Click handler for our "Click Me!" button. */
  private renderStar = (index: number): ReactNode => {
    const { hoverRating, rating } = this.state;
    if (hoverRating >= index) {
      return "★"; // Full star
    } else if (hoverRating + 0.5 === index) {
      return "☆"; // Half star
    } else {
      return "☆"; // Empty star
    }
  };

  private handleRatingChange = (rating: number): void => {
    // If the selected rating is equal to the current rating, toggle half stars
    if (rating === this.state.rating) {
      // If the current rating is a whole number, set it to a half-star
      if (rating % 1 === 0.5) {
        this.setState({ rating: Math.floor(rating) });
      }
      // If the current rating is a half-star, set it to the next whole number
      else {
        this.setState({ rating: rating + 0.5 });
      }
    }
    // Otherwise, set the rating to the selected value
    else {
      this.setState({ rating });
    }

    // Send the new rating value back to Streamlit
    Streamlit.setComponentValue(rating);
  };


  /** Focus handler for our "Click Me!" button. */
  private _onFocus = (): void => {
    this.setState({ isFocused: true })
  }

  /** Blur handler for our "Click Me!" button. */
  private _onBlur = (): void => {
    this.setState({ isFocused: false })
  }
}

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(StarRatings)
